const {accessSync, constants} = require('node:fs');
const dirname = __dirname.replaceAll('\\', '/');
const i = dirname.lastIndexOf('/server/');
const rootPath = i >= 0 ? dirname.substring(0, i + 7) : '';
function getRootPath() {
    try {
        accessSync(rootPath, constants.F_OK);
        return rootPath;
    }
    catch {
        return null;
    }
} 
module.exports = {
    getRootPath,
    rootPath,
};